import React, { Component, useState, useEffect } from 'react';


const useFetch = (url)=>{ 

    const[data, setData] = useState({});

    useEffect(() => {

        fetch(url)
        .then((res) => {
            return res.json();
        })
        .then((data) => {
            setData(data.meals);
        });
    }, [url]);

    return {data};

}

export default useFetch
