import React, { Component, useState, useEffect } from 'react';


const useFetch = (url)=>{ 

    const[data, setData] = useState(null);

    useEffect(() => {

        fetch(url)
        .then((res) => {
            return res.json();
        })
        .then((data) => {
            setPosts(data.meals);
        });
    }, [url]);

    return data;

}
