import React, { useState, useEffect } from 'react';
import useFetch from '../services/useFetch';

const Search = ()=>{ 

        const [query, setQuery] = useState('');
        const [datalist, setDatalist] = useState(null);

        function handleChange(e) {
            setQuery(e.target.value);
          
            fetch('https://www.themealdb.com/api/json/v1/1/search.php?s='+e.target.value)
            .then((res) => {
              return res.json();
            })
            .then((data) => {
                const recipes = data.meals;
                setDatalist(recipes);
            });

        }



        return (
            <>
                <form className="search-form m-4 p-3">
                    <div className="form-outline">
                        <input type="search" id="form1"  list="list-form1" className="form-control" onChange={handleChange} placeholder="Type search query" aria-label="Search" />
                        <datalist id="list-form1">
                            {datalist}
                        </datalist>
                    </div>
                </form>
            </>
        );
    }
 
export default Search;
