import React, { Component, useState, useEffect } from 'react';
import { Link } from "react-router-dom";
import { BrowserRouter as Router,Route,Routes, useParams } from "react-router-dom";

const SingleRecipe = ()=>{ 
    const params = useParams();

    const recipeId = params.id;

    const [recipe, setRecipe] = useState([]);

    useEffect(() => {

      fetch('https://www.themealdb.com/api/json/v1/1/lookup.php?i='+recipeId)
        .then((res) => {
          return res.json();
        })
        .then((data) => {
            const recipe = data.meals;
            console.log(recipe);
            setRecipe(recipe);
        });
    }, []);


        return (
        <div className="col-xxl-8 px-4 py-5">
            <div className="row flex-lg-row-reverse align-items-center g-5 py-5">
            <div className="col-10 col-sm-8 col-lg-6">
                <img src="bootstrap-themes.png" className="d-block mx-lg-auto img-fluid" alt="Bootstrap Themes" width="700" height="500" loading="lazy"/>
            </div>
            <div className="col-lg-6">
                <h1 className="display-5 fw-bold lh-1 mb-3">{recipe.strMeal}</h1>
                <p className="lead">{recipe.strInstructions}</p>
                <div className="d-grid gap-2 d-md-flex justify-content-md-start">
                <button type="button" className="btn btn-primary btn-lg px-4 me-md-2">Primary</button>
                </div>
            </div>
            </div>
        </div>
        );
    }
 
export default SingleRecipe;
